function onPress(imputName) {
  $.get("https://pokeapi.co/api/v2/pokemon/" + imputName, function (data) {
    console.log(data);
    console.log(data.sprites.other.showdown.front_default);
    document.getElementById("name").innerHTML = data.name;
    const image = document.createElement("img");
    image.src = data.sprites.other.showdown.front_default;
    $("#output").html(image);
  });
  
}
